/* FORM - INDEX.JS */

import React from 'react';
import './style.css';

class Form extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            name: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.name});
    }

    handleSubmit(event) {
        alert( this.state.name + this.state.file);
        event.preventDefault();
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Name video :
                    <input type="text" value={this.state.name} onChange={this.handleChange} />
                </label>
                <label>
                    URL video :
                    <input type="file" value={this.state.file} onChange={this.handleChange} />
                </label>
                <input className="-btn-submit" type="submit" value="Upload your video" />
            </form>
        );
    }
}

export default Form;

/* A CONTINUER : il faut recuperer les donnees renseignees lors du submit */
