/* FORM - INDEX.JS */

import React from 'react';
import './style.css';

class Form extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            name: '',
            url: ''
        };

        this.handleInputChange = this.handleInputChange.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);
    }

    // handleChange(event) {
    //     this.setState({name: event.target.name}, {url: event.target.url});
    // }

    handleSubmit(event) {
        alert('Votre video : ' + this.state.name + this.state.url);
        event.preventDefault();
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Name video :
                    <input type="text" value={this.state.name} onChange={this.handleChange} />
                </label>
                <label>
                    URL video :
                    <input type="url" value={this.state.url} onChange={this.handleChange} />
                </label>
                <input type="submit" value="Upload your video" />
            </form>
        );
    }
}

export default Form;

